body.script:
  const duration = 2 * 60 * 60 * 1000 + 30 * 60 * 1000; // 2 hours 30 minutes in ms
  let startTime = Date.now();

  function updateTimer() {
    let elapsed = Date.now() - startTime;
    let remaining = duration - elapsed;
    if (remaining <= 0) {
      clearInterval(timerInterval);
      document.getElementById("timer").innerHTML = "Time's up! The exam is now closed.";
      // Optionally disable submit button and inputs here
      document.querySelector("form").querySelectorAll("input, textarea, button").forEach(el => el.disabled = true);
      return;
    }
    let hrs = Math.floor(remaining / (1000 * 60 * 60));
    let mins = Math.floor((remaining % (1000 * 60 * 60)) / (1000 * 60));
    let secs = Math.floor((remaining % (1000 * 60)) / 1000);
    document.getElementById("timer").innerHTML = `Time remaining: ${hrs}h ${mins}m ${secs}s`;
  }

  updateTimer();
  let timerInterval = setInterval(updateTimer, 1000);
